{% block body_id 'error' %}

{% block main %}
    <h1 class=\"text-danger\">{{ 'http_error.name'|trans({ 'status_code': 500 }) }}</h1>

    <p class=\"lead\">
        {{ 'http_error_500.description'|trans }}
